  group_by(Sample) %>%
  summarise(mean_RNA = mean(RNA, na.rm = TRUE),
            ci_lower_RNA = mean(RNA, na.rm = TRUE) - 1.96 * sd(RNA, na.rm = TRUE) / sqrt(n()),
            ci_upper_RNA = mean(RNA, na.rm = TRUE) + 1.96 * sd(RNA, na.rm = TRUE) / sqrt(n()),
            mean_PFU = mean(PFU, na.rm = TRUE),
