## function combining the two functions above into a single functional output.
## Perform normality (Shapiro Wilk) and homogeneity of variance (Levene's) tests. 
## Takes data frame, a list of grouping variables, and list of 
## test variables to check for assumptions. P-value can also be adjusted.
## Identifies if assumptions are met for ANOVA v Kruskal or T v Wilcox tests
## and the R functions for the respective test. 
perform_assumption_checks <- function(data, group_vars, test_vars, p = 0.05) {
  normal_results <- data %>%
    group_by(across(all_of(group_vars))) %>%
    group_split() %>%
    map_df(~ {
      map(test_vars, function(var) {
        if (var %in% names(.x)) {  ## ensure column exists
          unique_values <- unique(na.omit(.x[[var]]))  ## get unique non-NA values
          ## run Shapiro-Wilk only if >1 unique value
          if (length(unique_values) > 1) {
            res <- shapiro.test(na.omit(.x[[var]]))
            normal_status <- ifelse(res$p.value > p, "Yes", "No")} 
          else {  ## handle identical values gracefully
            ## artificially set p-value to 0 (perfectly uniform/identical, but not normal)
            res <- list(statistic = NA, p.value = 0)  
            normal_status <- "No"}
          tibble(
            .x %>% dplyr::select(all_of(group_vars)) %>% distinct(),  ## keep distinct group vars
            Test_Variable = var,  
            W_Statistic = res$statistic,
            Shapiro_P_Value = format(res$p.value, scientific = FALSE),
            Normal = normal_status)} 
        else { NULL }})}) ## handle missing columns
  
  homogeneity_results <- data %>%
    mutate(across(all_of(group_vars), as.factor)) %>%  ## convert all grouping variables to factors
    filter(if_all(all_of(group_vars), ~ n_distinct(.) > 1)) %>%  ## ensure groups have more than one level
    group_split() %>%
    map_df(~ {
      map(test_vars, function(var) {
        group_var <- group_vars[1]  ## use first grouping variable for Levene's test
        formula <- as.formula(paste(var, "~", group_var))
        ## run Levene's test only if there are multiple levels
        if (length(unique(.x[[group_var]])) > 1) {
          test_result <- car::leveneTest(formula, data = .x)
          tibble(
            .x %>% dplyr::select(all_of(group_vars)) %>% distinct(),  ## dynamic group vars
            #.x %>% group_by(across(all_of(group_vars))) %>% summarise(across(everything(), first), .groups = "drop"),
            Test_Variable = var,  
            F_Statistic = test_result$"F value"[1],
            Levene_P_Value = format(test_result$"Pr(>F)"[1], scientific = FALSE),
            Homogeneous = ifelse(test_result$"Pr(>F)"[1] > p, "Yes", "No"))} 
        else {
          ## warning output if conditions are not be (i.e. too limited)
          tibble(
            .x %>% dplyr::select(all_of(group_vars)) %>% distinct(),
            Test_Variable = var,
            F_Statistic = NA,
            P_Value = NA,
            Homogeneity = "Insufficient levels")}})})
  
  ## combine normality & homogeneity results
  final_results <- full_join(normal_results, homogeneity_results, by = c(group_vars, "Test_Variable")) %>%
    ## identify the statistical tests that are appropriate
    mutate(Assumptions_Met = case_when(
      Normal == "Yes" & Homogeneous == "Yes" ~ "ANOVA | Student's T-test",
      Normal == "No" & Homogeneous == "Yes" ~ "Kruskal-Wallis | Wilcoxon-tests",
      Normal == "Yes" & Homogeneous == "No" ~ "Welch's ANOVA | Welch's t-test",
      Normal == "No" & Homogeneous == "No" ~ "Kruskal-Wallis | Wilcoxon-tests")) %>%
    ## identify the R functions for the appropriate statistical tests
    mutate(R_Function = str_extract_all(Assumptions_Met, 
                                        "ANOVA|Student's T-test|Kruskal-Wallis|Wilcoxon-tests|Welch's ANOVA|Welch's t-test"),
           R_Function = sapply(R_Function, function(tests) {
             ## only include valid non-NA function names
             paste(na.omit(c(
               ifelse("ANOVA" %in% tests, "aov()", NA),
               ifelse("Student's T-test" %in% tests, "t.test(var.equal = TRUE)", NA),
               ifelse("Kruskal-Wallis" %in% tests, "kruskal.test()", NA),
               ifelse("Wilcoxon-tests" %in% tests, "wilcox.test()", NA),
               ifelse("Welch's ANOVA" %in% tests, "oneway.test()", NA),
               ifelse("Welch's t-test" %in% tests, "t.test(var.equal = FALSE)", NA))),
               collapse = " | ")})) %>%
    ## add Posthoc_Tests column based on Assumptions_Met columns tests
    mutate(Posthoc_Tests = case_when(
      Assumptions_Met == "ANOVA | Student's T-test" ~ "ANOVA :: TukeyHSD or Dunnett's",
      Assumptions_Met == "Kruskal-Wallis | Wilcoxon-tests" ~ "Kruskal-Wallis :: Dunn's Test",
      Assumptions_Met == "Welch's ANOVA | Welch's t-test" ~ "Welch's ANOVA :: Games-Howell",
      TRUE ~ NA_character_))
  return(final_results)}

